position_horizontal, position_depth, aim = 0, 0, 0

input_array.each do |input|
  movement, amount = input.split(' ')
  amount = amount.to_i

  case movement
  when 'forward'
    position_horizontal += amount
    position_depth += (aim * amount)
  when 'down'
    aim  += amount
  when 'up'
    aim -= amount
  end
end

position_horizontal * position_depth

