# part 2 ======

def filter(input_array, most_common)
  position_in_input = 0
  input_array_selection = input_array.dup

  while input_array_selection.size > 1 do
    count_1 = 0
    input_array_selection.each { |input| count_1 += 1 if input[position_in_input] == '1' }

    if most_common
      to_select = (count_1 >= (input_array_selection.size / 2.0) ? '1' : '0')
    else
      to_select = (count_1 >= (input_array_selection.size / 2.0) ? '0' : '1')
    end

    input_array_selection.select! { |input| input[position_in_input] == to_select }
    position_in_input += 1
  end

  input_array_selection[0].to_i(2)
end

filter(input_array, true) * filter(input_array, false)
